import ManagerConsole from '../components/ActivityManagement/ManagerConsole.vue';
import MemberConsole from '../components/ActivityManagement/MemberConsole.vue';

const axios = require('axios').default;

const ACTIVITY_INFO_URL = 'http://localhost:4000/getActivityInfo';
const USER_ROLE = 'http://localhost:4000/getUserRole';
const QUIT_ACTIVITY = 'http://localhost:4000/quitActivity';
export default {
  components: {
    'manager-console': ManagerConsole,
    'member-console': MemberConsole
  },

  data() {
    return {
      activity_id: null,
      user_id: null,
      activity_info: [],
      user_role: ''
    };
  },

  provide() {
    return {
      activity_info: this.activity_info
    };
  },

  created() {
    // get the route parameter 'id' from router
    this.activity_id = this.$route.params.activity_id;
    this.user_id = this.$route.params.user_id;
  },

  methods: {
    /**
     * @description
     * Return activity info of a specific activity with the provided activity_id
     */
    getActivityInfo() {
      axios.post(ACTIVITY_INFO_URL, {
        activity_id: this.activity_id
      }).then(response => {
        this.activity_info.push(response.data);
      });
    },

    /**
     * @description
     * Return the role of a user with the provided activity_id and user_id
     */
    getUserRole() {
      axios.post(USER_ROLE, {
        activity_id: this.activity_id,
        user_id: this.user_id
      }).then(response => {
        this.user_role = response.data.role;
      });
    },

    /**
     * @description
     * Quit from the activity with provided activity_id and user_id
     */
    quitActivity() {
      axios.post(QUIT_ACTIVITY, {
        activity_id: this.activity_id,
        user_id: this.user_id
      });
    }

  },
  computed: {
    console() {
      if (this.user_role === 'MANAGER') {
        return 'manager-console';
      }

      return 'member-console';
    }

  },

  mounted() {
    this.getActivityInfo();
    this.getUserRole();
  }

};
